import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from "react-router-dom";
import Register from "./components/Register";
import Login from "./components/Login";
import Home from "./components/Home";
import Navbar from "./components/Navbar";

const App = () => {
  const isLoggedIn = localStorage.getItem("isLoggedIn") === "true";  // Check if logged in

  const handleLogout = () => {
    localStorage.removeItem("isLoggedIn");  // Remove login state from localStorage
    navigate("/login");  // Redirect to login page
  };

  return (
    <Router>
      {isLoggedIn && <Navbar />}  {/* Show Navbar only when logged in */}

      <Routes>
        <Route path="/register" element={<Register />} />
        <Route path="/login" element={<Login />} />
        <Route
          path="/home"
          element={isLoggedIn ? <Home /> : <Navigate to="/login" />}
        />
        <Route path="/create" element={<div>Create Album Page</div>} />
        <Route path="/profile" element={<div>Profile Page</div>} />
        <Route path="/" element={<Navigate to="/login" replace />} />
        <Route path="*" element={<div>404 Not Found</div>} />
      </Routes>

      {/* Logout Button (Visible only if logged in) */}
      {isLoggedIn && (
        <button onClick={handleLogout} style={{ position: "absolute", top: "10px", right: "10px" }}>
          Logout
        </button>
      )}
    </Router>
  );
};

export default App;
