import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Register from "./components/Register";
import Login from "./components/Login";
import Home from "./components/Home";
import Navbar from "./components/Navbar";

const App = () => {
  const isLoggedIn = localStorage.getItem("isLoggedIn") === "true";

  return (
    <Router>
      {/* Show Navbar only when user is logged in and not on the login or register pages */}
      {isLoggedIn && <Navbar />} {/* Navbar will be shown only if the user is logged in */}

      <Routes>
        <Route path="/register" element={isLoggedIn ? <Navigate to="/home" replace /> : <Register />} />
        <Route path="/login" element={isLoggedIn ? <Navigate to="/home" replace /> : <Login />} />

        {/* Protected routes */}
        <Route
          path="/home"
          element={isLoggedIn ? <Home /> : <Navigate to="/login" replace />}
        />
        <Route
          path="/create"
          element={isLoggedIn ? <div>Create Album Page</div> : <Navigate to="/login" replace />}
        />
        <Route
          path="/profile"
          element={isLoggedIn ? <div>Profile Page</div> : <Navigate to="/login" replace />}
        />

        {/* Default route */}
        <Route path="/" element={<Navigate to="/login" replace />} />

        {/* Catch-all route for undefined paths */}
        <Route path="*" element={<div>404 Not Found</div>} />
      </Routes>
    </Router>
  );
};

export default App;
