import React, { useState } from 'react';
import initialImages from '../assets/images'; // Import images array

let nextId = 5;

function ImageUploader() {
  const [images, setImages] = useState(initialImages);
  const [title, setTitle] = useState('');
  const [tags, setTags] = useState('');
  const [ingredients, setIngredients] = useState('');
  const [file, setFile] = useState(null);

  const handleUpload = () => {
    if (!file) return alert("Please upload an image.");

    const reader = new FileReader();
    reader.onloadend = () => {
      const newImage = {
        id: nextId++,
        title: title.trim(),
        url: reader.result,
        tags: tags.split(',').map(tag => tag.trim()),
        ingredients: ingredients.split(',').map(ing => ing.trim())
      };
      setImages(prev => [...prev, newImage]);
      setTitle('');
      setTags('');
      setIngredients('');
      setFile(null);
    };
    reader.readAsDataURL(file);
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Add New Image</h2>
      <input type="text" placeholder="Title" value={title} onChange={e => setTitle(e.target.value)} /><br />
      <input type="text" placeholder="Tags (comma separated)" value={tags} onChange={e => setTags(e.target.value)} /><br />
      <input type="text" placeholder="Ingredients (comma separated)" value={ingredients} onChange={e => setIngredients(e.target.value)} /><br />
      <input type="file" accept="image/*" onChange={e => setFile(e.target.files[0])} /><br />
      <button onClick={handleUpload}>Add Image</button>

      {/* <h3>Image Gallery</h3>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: 20 }}>
        {images.map(image => (
          <div key={image.id} style={{ border: '1px solid #ccc', padding: 10, width: 200 }}>
            <img
              src={typeof image.url === 'string' ? image.url : image.url.default}
              alt={image.title}
              style={{ width: '100%', height: 150, objectFit: 'cover' }}
            />
            <h4>{image.title}</h4>
            <p><strong>Tags:</strong> {image.tags.join(', ')}</p>
            <p><strong>Ingredients:</strong> {image.ingredients.join(', ')}</p>
          </div>
        ))}
      </div> */}
    </div>
  );
}

export default ImageUploader;
